// src/components/NowPlaying.vue
<template>
  <div class="now-playing-container">
    <div v-if="!currentTrack" class="no-track">
      <p>No track currently playing</p>
      <button @click="refreshTrack" class="refresh-button">Refresh</button>
    </div>
    
    <div v-else class="track-card">
      <img :src="currentTrack.album.images[0].url" alt="Album Cover" class="album-cover" />
      
      <div class="track-info">
        <h2>{{ currentTrack.name }}</h2>
        <p class="artist">{{ artistNames }}</p>
        <p class="album">{{ currentTrack.album.name }}</p>
        
        <div class="progress-container">
          <div class="progress-bar">
            <div class="progress" :style="{ width: progressPercentage + '%' }"></div>
          </div>
          <div class="time-info">
            <span>{{ formatTime(progressMs) }}</span>
            <span>{{ formatTime(currentTrack.duration_ms) }}</span>
          </div>
        </div>
      </div>
      
      <MusicPlayer />
    </div>
  </div>
</template>

<script>
import MusicPlayer from './MusicPlayer.vue'

export default {
  name: 'NowPlaying',
  components: {
    MusicPlayer
  },
  data() {
    return {
      progressMs: 0,
      progressInterval: null
    }
  },
  computed: {
    currentTrack() {
      return this.$store.state.currentTrack
    },
    isPlaying() {
      return this.$store.state.isPlaying
    },
    artistNames() {
      if (!this.currentTrack) return ''
      return this.currentTrack.artists.map(artist => artist.name).join(', ')
    },
    progressPercentage() {
      if (!this.currentTrack) return 0
      return (this.progressMs / this.currentTrack.duration_ms) * 100
    }
  },
  methods: {
    formatTime(ms) {
      const seconds = Math.floor((ms / 1000) % 60)
      const minutes = Math.floor((ms / (1000 * 60)) % 60)
      
      return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`
    },
    refreshTrack() {
      this.$store.dispatch('fetchCurrentTrack')
    },
    startProgressTimer() {
      if (this.progressInterval) {
        clearInterval(this.progressInterval)
      }
      
      this.progressInterval = setInterval(() => {
        if (this.isPlaying && this.progressMs < this.currentTrack.duration_ms) {
          this.progressMs += 1000
        } else if (this.progressMs >= this.currentTrack.duration_ms) {
          this.refreshTrack()
        }
      }, 1000)
    }
  },
  created() {
    this.refreshTrack()
    
    // Poll for track updates every 30 seconds
    this.trackInterval = setInterval(() => {
      this.refreshTrack()
    }, 30000)
  },
  watch: {
    currentTrack(newTrack) {
      if (newTrack) {
        // Start progress timer
        this.startProgressTimer()
      }
    },
    isPlaying(playing) {
      if (playing) {
        this.startProgressTimer()
      } else if (this.progressInterval) {
        clearInterval(this.progressInterval)
      }
    }
  },
  beforeUnmount() {
    if (this.progressInterval) {
      clearInterval(this.progressInterval)
    }
    
    if (this.trackInterval) {
      clearInterval(this.trackInterval)
    }
  }
}
</script>

<style scoped>
.now-playing-container {
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
}

.no-track {
  text-align: center;
  background-color: #282828;
  padding: 2rem;
  border-radius: 8px;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
}

.refresh-button {
  background-color: #1DB954;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 30px;
  margin-top: 1rem;
  cursor: pointer;
}

.track-card {
  display: flex;
  flex-direction: column;
  background-color: #282828;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
}

.album-cover {
  width: 100%;
  max-height: 300px;
  object-fit: cover;
}

.track-info {
  padding: 1.5rem;
  text-align: left;
}

h2 {
  margin: 0;
  font-size: 1.5rem;
  color: white;
}

.artist {
  color: #1DB954;
  margin: 0.5rem 0;
}

.album {
  color: #b3b3b3;
  margin-bottom: 1.5rem;
}

.progress-container {
  margin-top: 1rem;
}

.progress-bar {
  height: 4px;
  background-color: #535353;
  border-radius: 2px;
  overflow: hidden;
}

.progress {
  height: 100%;
  background-color: #1DB954;
}

.time-info {
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: #b3b3b3;
  margin-top: 0.5rem;
}

@media (min-width: 768px) {
  .track-card {
    flex-direction: row;
  }
  
  .album-cover {
    width: 250px;
    height: 250px;
  }
}
</style>
