// src/components/NowPlaying.vue - Updated version
<template>
  <div class="now-playing-container">
    <div v-if="!currentTrack" class="no-track">
      <p>No track currently playing</p>
      <button @click="refreshTrack" class="refresh-button">Refresh</button>
    </div>
    
    <div v-else class="track-card">
      <img :src="currentTrack.album.images[0].url" alt="Album Cover" class="album-cover" />
      
      <div class="track-info">
        <h2>{{ currentTrack.name }}</h2>
        <p class="artist">{{ artistNames }}</p>
        <p class="album">{{ currentTrack.album.name }}</p>
        
        <div class="progress-container">
          <div class="progress-bar">
            <div class="progress" :style="{ width: progressPercentage + '%' }"></div>
          </div>
          <div class="time-info">
            <span>{{ formatTime(progressMs) }}</span>
            <span>{{ formatTime(currentTrack.duration_ms) }}</span>
          </div>
        </div>
      </div>
      
      <MusicPlayer />
    </div>
  </div>
</template>

<script>
import MusicPlayer from './MusicPlayer.vue'

export default {
  name: 'NowPlaying',
  components: {
    MusicPlayer
  },
  data() {
    return {
      progressMs: 0,
      progressInterval: null,
      pollingInterval: null,
      pollingFrequency: 1000 // Poll every second for accurate progress
    }
  },
  computed: {
    currentTrack() {
      return this.$store.state.currentTrack
    },
    isPlaying() {
      return this.$store.state.isPlaying
    },
    artistNames() {
      if (!this.currentTrack) return ''
      return this.currentTrack.artists.map(artist => artist.name).join(', ')
    },
    progressPercentage() {
      if (!this.currentTrack) return 0
      return (this.progressMs / this.currentTrack.duration_ms) * 100
    }
  },
  methods: {
    formatTime(ms) {
      const seconds = Math.floor((ms / 1000) % 60)
      const minutes = Math.floor((ms / (1000 * 60)) % 60)
      
      return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`
    },
    async refreshTrack() {
      await this.$store.dispatch('fetchCurrentPlayback')
    },
    startProgressTimer() {
      // Clear any existing intervals
      this.clearIntervals()
      
      // Only start polling if we have a track and it's playing
      if (this.currentTrack && this.isPlaying) {
        // Start polling for playback state
        this.pollingInterval = setInterval(async () => {
          await this.refreshTrack()
        }, this.pollingFrequency)
        
        // Use local timer for smoother UI updates between API calls
        this.progressInterval = setInterval(() => {
          if (this.isPlaying && this.progressMs < this.currentTrack.duration_ms) {
            this.progressMs += 100 // Update every 100ms for smooth animation
          }
        }, 100)
      }
    },
    clearIntervals() {
      if (this.progressInterval) {
        clearInterval(this.progressInterval)
        this.progressInterval = null
      }
      
      if (this.pollingInterval) {
        clearInterval(this.pollingInterval)
        this.pollingInterval = null
      }
    }
  },
  async created() {
    // Initial fetch
    await this.refreshTrack()
    
    // Start progress timer
    this.startProgressTimer()
  },
  watch: {
    currentTrack(newTrack, oldTrack) {
      if (newTrack) {
        // If it's a different track or first load
        if (!oldTrack || newTrack.id !== oldTrack.id) {
          this.progressMs = this.$store.state.progressMs || 0
          this.startProgressTimer()
        }
      } else {
        this.clearIntervals()
      }
    },
    isPlaying(playing) {
      if (playing) {
        this.startProgressTimer()
      } else {
        this.clearIntervals()
      }
    }
  },
  beforeUnmount() {
    this.clearIntervals()
  }
}
</script>

