// src/components/LoginPage.vue
<template>
  <div class="login-container">
    <div class="login-card">
      <div class="spotify-logo">
        <!-- Replace image with a simple div that has Spotify's logo styling -->
        <div class="spotify-icon"></div>
      </div>
      <h1>Spotify Now Playing</h1>
      <p>Connect your Spotify account to see what you're currently listening to</p>
      <button @click="login" class="login-button">
        Connect with Spotify
      </button>
    </div>
  </div>
</template>

<script>
export default {
  name: 'LoginPage',
  methods: {
    login() {
      const CLIENT_ID = process.env.VUE_APP_SPOTIFY_CLIENT_ID
      const REDIRECT_URI = process.env.VUE_APP_REDIRECT_URI || `${window.location.origin}/callback`
      const scopes = [
        'user-read-currently-playing',
        'user-read-playback-state'
      ]

      const authUrl = new URL('https://accounts.spotify.com/authorize')
      authUrl.searchParams.append('client_id', CLIENT_ID)
      authUrl.searchParams.append('response_type', 'code')
      authUrl.searchParams.append('redirect_uri', REDIRECT_URI)
      authUrl.searchParams.append('scope', scopes.join(' '))

      window.location.href = authUrl.toString()
    }
  }
}
</script>

<style scoped>
.login-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.login-card {
  background-color: #282828;
  padding: 2rem;
  border-radius: 8px;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
  max-width: 400px;
}

.spotify-logo {
  display: flex;
  justify-content: center;
  margin-bottom: 1rem;
}

.spotify-icon {
  width: 60px;
  height: 60px;
  background-color: #1DB954;
  border-radius: 50%;
  position: relative;
}

.spotify-icon:after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-40%, -50%);
  width: 0;
  height: 0;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  border-left: 16px solid white;
}

h1 {
  color: #1DB954;
  margin-bottom: 1rem;
}

p {
  margin-bottom: 2rem;
  color: #b3b3b3;
}

.login-button {
  background-color: #1DB954;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 30px;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s;
}

.login-button:hover {
  background-color: #1ed760;
}
</style>
