// src/components/CallbackPage.vue
<template>
  <div class="callback-container">
    <div v-if="loading" class="loading">
      <div class="spinner"></div>
      <p>Connecting to Spotify...</p>
    </div>
    <div v-if="error" class="error-message">
      <p>{{ error }}</p>
      <button @click="returnToLogin" class="retry-button">Return to Login</button>
    </div>
  </div>
</template>

<script>
export default {
  name: 'CallbackPage',
  data() {
    return {
      loading: true,
      error: null
    }
  },  created() {
    this.handleCallback()
  },
  methods: {
    async handleCallback() {
      const urlParams = new URLSearchParams(window.location.search)
      const code = urlParams.get('code')
      const error = urlParams.get('error')
      
      if (error) {
        this.error = `Spotify authentication error: ${error}`
        this.loading = false
        return
      }
      
      if (!code) {
        this.error = "No authorization code received from Spotify"
        this.loading = false
        return
      }
      
      try {
        // Import the auth proxy service
        const { exchangeCodeForToken, getRedirectUri } = await import('@/services/authProxy')
        
        // Get the redirect URI from the auth proxy
        const REDIRECT_URI = getRedirectUri()
        
        console.log("Using redirect URI:", REDIRECT_URI)
        
        // Exchange the code for a token without exposing client secret in browser
        const tokenData = await exchangeCodeForToken(code, REDIRECT_URI)
          .catch(error => {
            throw new Error(`Token exchange failed: ${error.message}`)
          })
          
        console.log("Token exchange successful")
          if (!response.ok) {
          console.error("Token exchange failed:", responseText)
          
          // Check if this is likely a client ID or configuration issue
          if (responseText.includes('invalid_client') || response.status === 401) {
            this.error = `Authentication failed: Invalid client credentials. Please check the configuration.`
            this.loading = false
            
            // Add diagnostics button
            setTimeout(() => {
              const errorDiv = document.querySelector('.error-message')
              if (errorDiv) {
                const diagButton = document.createElement('button')
                diagButton.className = 'retry-button diagnostics-button'
                diagButton.innerText = 'Run Diagnostics'
                diagButton.addEventListener('click', () => this.$router.push('/diagnostics'))
                errorDiv.appendChild(diagButton)
              }
            }, 100)
            
            return
          }
          
          this.error = `Failed to exchange code for tokens: ${response.status} ${responseText}`
          this.loading = false
          return
        }
        
        try {
          const data = JSON.parse(responseText)
          console.log("Token received successfully")
          
          this.$store.commit('setToken', data.access_token)
          this.$store.commit('setRefreshToken', data.refresh_token)
          
          this.$router.push('/')
        } catch (parseError) {
          console.error("Error parsing response:", parseError)
          this.error = "Error parsing Spotify response"
          this.loading = false
        }
      } catch (error) {
        console.error('Error during authentication:', error)
        this.error = `Authentication error: ${error.message}`
        this.loading = false
      }
    },
    
    returnToLogin() {
      this.$router.push('/login')
    }
  }
}
</script>

<style scoped>
.callback-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #121212;
  color: white;
}

.loading {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.spinner {
  width: 40px;
  height: 40px;
  border: 4px solid rgba(255, 255, 255, 0.1);
  border-radius: 50%;
  border-top-color: #1DB954;
  animation: spin 1s ease-in-out infinite;
  margin-bottom: 20px;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

.error-message {
  background-color: #282828;
  padding: 2rem;
  border-radius: 8px;
  max-width: 500px;
  text-align: center;
}

.retry-button {
  background-color: #1DB954;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 30px;
  font-weight: bold;
  cursor: pointer;
  margin-top: 20px;
}

.retry-button:hover {
  background-color: #1ed760;
}

.diagnostics-button {
  margin-top: 1rem;
  background-color: #535353;
}

.diagnostics-button:hover {
  background-color: #646464;
}
</style>
