// src/components/MusicPlayer.vue
<template>
  <div class="player-controls">
    <button @click="previous" class="control-button">
      <i class="fas fa-step-backward"></i>
    </button>
    
    <button @click="togglePlayPause" class="play-button">
      <i :class="isPlaying ? 'fas fa-pause' : 'fas fa-play'"></i>
    </button>
    
    <button @click="next" class="control-button">
      <i class="fas fa-step-forward"></i>
    </button>
  </div>
</template>

<script>
export default {
  name: 'MusicPlayer',
  computed: {
    isPlaying() {
      return this.$store.state.isPlaying
    }
  },
  methods: {
    async togglePlayPause() {
      if (!this.$store.state.token) return
      
      try {
        const endpoint = this.isPlaying 
          ? 'https://api.spotify.com/v1/me/player/pause'
          : 'https://api.spotify.com/v1/me/player/play'
        
        const response = await fetch(endpoint, {
          method: 'PUT',
          headers: {
            'Authorization': `Bearer ${this.$store.state.token}`
          }
        })
        
        if (response.status === 204) {
          this.$store.commit('setIsPlaying', !this.isPlaying)
        }
      } catch (error) {
        console.error('Error toggling playback:', error)
      }
    },
    
    async previous() {
      if (!this.$store.state.token) return
      
      try {
        await fetch('https://api.spotify.com/v1/me/player/previous', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${this.$store.state.token}`
          }
        })
        
        // Wait a moment for Spotify to update
        setTimeout(() => {
          this.$store.dispatch('fetchCurrentTrack')
        }, 500)
      } catch (error) {
        console.error('Error skipping to previous track:', error)
      }
    },
    
    async next() {
      if (!this.$store.state.token) return
      
      try {
        await fetch('https://api.spotify.com/v1/me/player/next', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${this.$store.state.token}`
          }
        })
        
        // Wait a moment for Spotify to update
        setTimeout(() => {
          this.$store.dispatch('fetchCurrentTrack')
        }, 500)
      } catch (error) {
        console.error('Error skipping to next track:', error)
      }
    }
  }
}
</script>

<style scoped>
.player-controls {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem;
  background-color: #181818;
}

.control-button, .play-button {
  background: none;
  border: none;
  color: #b3b3b3;
  font-size: 1.5rem;
  cursor: pointer;
  margin: 0 1rem;
  transition: color 0.3s;
}

.control-button:hover, .play-button:hover {
  color: white;
}

.play-button {
  font-size: 2rem;
  color: #1DB954;
}

.play-button:hover {
  color: #1ed760;
}
</style>
