// src/components/CallbackPage.vue
<template>
  <div class="callback-container">
    <div v-if="loading" class="loading">
      <div class="spinner"></div>
      <p>Connecting to Spotify...</p>
    </div>
    <div v-if="error" class="error-message">
      <p>{{ error }}</p>
      <button @click="returnToLogin" class="retry-button">Return to Login</button>
    </div>
  </div>
</template>

<script>
export default {
  name: 'CallbackPage',
  data() {
    return {
      loading: true,
      error: null
    }
  },
  created() {
    this.handleCallback()
  },
  methods: {
    async handleCallback() {
      const urlParams = new URLSearchParams(window.location.search)
      const code = urlParams.get('code')
      const error = urlParams.get('error')
      
      if (error) {
        this.error = `Spotify authentication error: ${error}`
        this.loading = false
        return
      }
      
      if (!code) {
        this.error = "No authorization code received from Spotify"
        this.loading = false
        return
      }
      
      try {
        const CLIENT_ID = process.env.VUE_APP_SPOTIFY_CLIENT_ID
        const CLIENT_SECRET = process.env.VUE_APP_SPOTIFY_CLIENT_SECRET
        const REDIRECT_URI = process.env.VUE_APP_REDIRECT_URI || `${window.location.origin}/callback`
        
        console.log("Using client ID:", CLIENT_ID)
        console.log("Using redirect URI:", REDIRECT_URI)
        // Don't log client secret
        
        // Create authorization string
        const authString = `${CLIENT_ID}:${CLIENT_SECRET}`
        const base64Auth = btoa(authString)
        
        const response = await fetch('https://accounts.spotify.com/api/token', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
            'Authorization': `Basic ${base64Auth}`
          },
          body: new URLSearchParams({
            grant_type: 'authorization_code',
            code,
            redirect_uri: REDIRECT_URI
          })
        })
        
        const responseText = await response.text()
        console.log("Response status:", response.status)
        
        if (!response.ok) {
          console.error("Token exchange failed:", responseText)
          this.error = `Failed to exchange code for tokens: ${response.status} ${responseText}`
          this.loading = false
          return
        }
        
        try {
          const data = JSON.parse(responseText)
          console.log("Token received successfully")
          
          this.$store.commit('setToken', data.access_token)
          this.$store.commit('setRefreshToken', data.refresh_token)
          
          this.$router.push('/')
        } catch (parseError) {
          console.error("Error parsing response:", parseError)
          this.error = "Error parsing Spotify response"
          this.loading = false
        }
      } catch (error) {
        console.error('Error during authentication:', error)
        this.error = `Authentication error: ${error.message}`
        this.loading = false
      }
    },
    
    returnToLogin() {
      this.$router.push('/login')
    }
  }
}
</script>

<style scoped>
.callback-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #121212;
  color: white;
}

.loading {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.spinner {
  width: 40px;
  height: 40px;
  border: 4px solid rgba(255, 255, 255, 0.1);
  border-radius: 50%;
  border-top-color: #1DB954;
  animation: spin 1s ease-in-out infinite;
  margin-bottom: 20px;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

.error-message {
  background-color: #282828;
  padding: 2rem;
  border-radius: 8px;
  max-width: 500px;
  text-align: center;
}

.retry-button {
  background-color: #1DB954;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 30px;
  font-weight: bold;
  cursor: pointer;
  margin-top: 20px;
}

.retry-button:hover {
  background-color: #1ed760;
}
</style>
